    fileReader.readAsText(file)
  }

  render()
  {
    return(
      <div>
        <label className='qrcode-text-btn'>
          <input type='file' accept='image/*' capture='environment' onChange={e => 
            onQRCamera(e.target.files[0])}></input>
        </label>
        <input type='text' className='qrcode-text'></input>
      </div>
    )
  }
}

export default App;


