import React from 'react';
import './index.css';
import jsQR from "jsqr";

class Canvas extends React.Component {

  getImageDataScaled = (imageElement) => {
    const maxWidth = 1920
    const maxHeight = 1080
    const scalingRatio = Math.min(
      1, 
      maxWidth / imageElement.naturalWidth, 
      maxHeight / imageElement.naturalHeight
    )

    const width = imageElement.naturalWidth * scalingRatio
    const height = imageElement.naturalHeight * scalingRatio

    const canvas = this.refs.canvas
    canvas.width = width
    canvas.height = height

    const canvasCtx = canvas.getContext('2d')
    canvasCtx.drawImage(imageElement, 0, 0, width, height)

    return canvasCtx.getImageData(0, 0, width, height)
  }

  componentDidMount() {
    const imageElement = this.refs.image
    imageElement.onload = () => {

      const width = 256//imageElement.naturalWidth
      const height =256// imageElement.naturalHeight

      //get the doc's canvas and set the widtha nd height
      const canvas = this.refs.canvas
      canvas.width = width
      canvas.height = height

      //get the context and draw the image at 0,0
      const canvasCtx = canvas.getContext('2d')
      canvasCtx.drawImage(imageElement, 0, 0, width, height)

      const imgData = canvasCtx.getImageData(0, 0, width, height)
      const imgArray = new Uint8Array(imgData.data);

      const code = jsQR(imgArray, width, height);
      if (code) {
        this.props.onQRCodeChange(code.data);
      } else {
        this.props.onQRCodeChange("No QR Code found");
      }
    }
  }


  render() {
      return(
        <div>
          <canvas ref="canvas" className="hidden" />
          <img ref="image" src={this.props.image} alt="canvas" className="hidden" />
        </div>
      )
    }
}
export default Canvas
