import React, { Component } from 'react';
import QrReader from "react-qr-reader";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      delay: 300,
      result: "No result"
    };
    this.handleScan = this.handleScan.bind(this);
  }
  handleScan(data) {
    if (data) {
      this.setState({
        result: data
      });
    }
  }
  handleError(err) {
    console.error(err);
  }

  onScanClick = (event) => {
    event.preventDefault()
    event.stopPropagation()
    console.log("scan clicked")
  };

  render() {
    return (
      <div>
        <button onClick={this.onScanClick}>Scan</button>
        <button>Enter</button>  
        <p>{this.state.result}</p>
      </div>
    );
  }
}

export default App;
