import React, { Component } from 'react';

class App extends Component {

  onScanClick = (event) => {
    event.preventDefault()
    event.stopPropagation()
    console.log("scan clicked")
  };

  render() {
    return (
      <div>
        <button onClick={this.onScanClick}>Scan</button>
        <button>Enter</button>  
        <script>
            function tryIt () {
                require(['qr-code-scanner'], function (QRScanner) {
                    QRScanner.initiate({
                        match: /^[a-zA-Z0-9]{16,18}$/,
                        onResult: function (result) { console.info('DONE: ', result); },
                        onError: function (err) { console.error('ERR :::: ', err); },
                        onTimeout: function () { console.warn('TIMEDOUT'); },
                    });  
                })
            }
        </script>
      </div>
    );
  }
}

export default App;
