import React, { Component } from 'react';
import jsQR from "jsqr";

class App extends Component {

  render()
  {
    return(
      <div>
        <label class='qrcode-text-btn'>
          <input type='file' accept='image/*' capture='environment' onChange='openQRCamera(this);'></input>
        </label>
        <input type='text' class='qrcode-text'></input>
      </div>
    )
  }
}

export default App;


